import React, { useState } from 'react';
import axios from 'axios';

function TeamFixtures() {
    const [team, setTeam] = useState('');
    const [fixtures, setFixtures] = useState([]);
    const [error, setError] = useState('');

    const fetchFixtures = async () => {
        try {
            const apiKey = process.env.REACT_APP_API_KEY;
            console.log('üîê Enviando x-api-key:', apiKey);
            const res = await axios.get(`/api/fixtures?team=${team}`, {
                headers: {
                    'x-api-key': process.env.REACT_APP_API_KEY
                }
            });

            setFixtures(res.data.response);
            setError('');
        } catch (err) {
            setError('Erro ao buscar jogos. Verifique o nome do time.');
            setFixtures([]);
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-4 text-white">
            <h1 className="text-2xl font-bold mb-4">üîç Buscar Jogos por Time</h1>

            <div className="flex mb-4">
                <input
                    type="text"
                    placeholder="Digite o nome do time, ex: Arsenal"
                    value={team}
                    onChange={(e) => setTeam(e.target.value)}
                    className="p-2 rounded text-black w-full"
                />
                <button
                    onClick={fetchFixtures}
                    className="ml-2 px-4 py-2 bg-blue-600 rounded hover:bg-blue-700"
                >
                    Buscar
                </button>
            </div>

            {error && <p className="text-red-400 mb-4">{error}</p>}

            {fixtures.map((fixture) => (
                <div key={fixture.fixture.id} className="bg-gray-800 rounded p-4 mb-2 shadow">
                    <div className="flex justify-between">
                        <span>{fixture.teams.home.name} vs {fixture.teams.away.name}</span>
                        <span>{new Date(fixture.fixture.date).toLocaleString()}</span>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default TeamFixtures;
