import React, { useEffect, useState } from 'react';
import axios from 'axios';

function FixtureWithOdds({ fixtureId }) {
  const [odds, setOdds] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOdds = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/odds/${fixtureId}`, {
          headers: {
            'x-api-key': process.env.REACT_APP_API_KEY
          }
        });

        setOdds(response.data);
      } catch (error) {
        console.error('Erro ao buscar odds:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchOdds();
  }, [fixtureId]);

  if (loading) return <p className="text-gray-400">üîÑ Carregando odds...</p>;

  if (!Array.isArray(odds) || odds.length === 0)
    return <p className="text-gray-500">‚ùå Odds n√£o dispon√≠veis para este jogo.</p>;

  return (
    <div className="mt-6">
      <h2 className="text-xl font-semibold mb-4">üí∏ Odds por Casa de Aposta</h2>
      <ul className="space-y-2">
        {odds.map((item, index) => (
          <li key={index} className="bg-gray-800 p-3 rounded text-sm text-white shadow">
            <strong className="text-yellow-400">{item.bookmaker}</strong>:{" "}
            <span className="ml-2">
              üè† <strong>Home:</strong> {item.odds?.home ?? "-"} |
              ü§ù <strong>Draw:</strong> {item.odds?.draw ?? "-"} |
              üö© <strong>Away:</strong> {item.odds?.away ?? "-"}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FixtureWithOdds;
