import React, { useEffect, useState } from 'react';
import axios from 'axios';

function FixtureWithOdds({ fixtureId }) {
  const [odds, setOdds] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOdds = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/odds/${fixtureId}`);
        setOdds(response.data);
      } catch (error) {
        console.error('Erro ao buscar odds:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchOdds();
  }, [fixtureId]);

  if (loading) return <p className="text-gray-400">Carregando odds...</p>;

  if (!odds.length) return <p className="text-gray-500">Odds não disponíveis.</p>;

  return (
    <div className="mt-2">
      <h4 className="font-semibold mb-1">Odds por casa de aposta:</h4>
      <ul className="space-y-1">
        {odds.map((item, index) => (
          <li key={index} className="text-sm bg-gray-800 p-2 rounded">
            <span className="text-yellow-400 font-bold">{item.bookmaker}</span>: 
            Home {item.odds.home} | Draw {item.odds.draw} | Away {item.odds.away}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FixtureWithOdds;
